import { useContext } from "react";
import {BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";

import { AuthContext } from "./context/AuthContext";
import Error from "./pages/Error/Error";
import Login from "./pages/Login/Login";
import MainPage from "./pages/MainPage/MainPage";
import Profile from "./pages/Profile/Profile";
import Register from "./pages/Register/Register";
import Department from './pages/Department/Department'
import Information from "./pages/Information/Information";
import Reports from "./pages/Reports/Reports";
import Staff from "./components/Staff/Staff";




function App() {

  const {user} = useContext(AuthContext)

  const userStorage = JSON.parse(localStorage.getItem('user'))
/*console.log('userStorage=',userStorage)*/


  const authUser = user || userStorage

  return (
    <Router>
      <Switch>
        <Route exact path='/'>
          { authUser ? <MainPage /> : <Redirect to='/login'/>}
        </Route>
        <Route path='/login'>
          { authUser ? <Redirect to={`/department/${authUser.main_department}`}/> : <Login/> }
        </Route>
        <Route path='/register'>
          { authUser ? <Redirect to='/'/> : <Register/> }
        </Route>
        <Route path='/profile/:username'>
          { authUser ? <Profile /> : <Login/> }
        </Route>
        <Route path='/department/:username'>
          { authUser ? <Department/> : <Login/> }
        </Route>
        <Route path='/Information'>
          { authUser ? <Information/> : <Login/> }
        </Route>
        <Route path='/Reports/'>
          { authUser ? <Reports/> : <Login/> }
        </Route>
        <Route path='/allUsers'>
          { authUser ? <Department/> : <Login/> }
        </Route>
        <Route path='/not_workedToday'>
          { authUser ? <Department/> : <Login/> }
        </Route>
        <Route path='/not_worked_yesterday'>
          { authUser ? <Department/> : <Login/> }
        </Route>
        <Route path='/sick_leave_today'>
          { authUser ? <Department/> : <Login/> }
        </Route>
        <Route path='/sick_leave_yesterday'>
          { authUser ? <Department/> : <Login/> }
        </Route>
        <Route path='/vacation_today'>
          { authUser ? <Department/> : <Login/> }
        </Route>
        <Route path='/vacation_yesterday'>
          { authUser ? <Department/> : <Login/> }
        </Route>

        <Route path='/*'>
          <Error/>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
