import {useEffect,useState} from 'react'
import axios from 'axios'
import Employee from '../Employee/Employee'
import './Staff.css'
import {Link, useParams} from 'react-router-dom'
import Modal from '../Templates/Modal/Modal'



export default function Staff() {
    const [seeChilds, setSeeChilds] = useState(false);
    const [checked, setChecked] = useState(false);
    const [statisticsWork, setStatisticsWork] = useState([]);
    const [users, setUsers] = useState([]);
    const [UsersDep, setUsersDeps] = useState([]);
    const [us, setUs] = useState([]);
    const [user, setUser] = useState([]);
    const [deps, setDeps] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [isJob, setIsJob] = useState(true);
    const [isFetching, setIsFetching] = useState(false);
    const [modalMain, setModalMain] = useState(false);

    const { username: departId } = useParams();
    const localUser = JSON.parse(localStorage.getItem('user'));
    const { username: depsId } = useParams();

    // фильтр нужных департаментов
    const currentDep = deps.filter(dep => departId ? dep._id === departId : dep._id === localUser.departmentId);
    let filterDeps = deps.filter(dep => currentDep.find(i => i === dep.dep_id || i === currentDep[0].dep_id));
    filterDeps.push(...currentDep);

    useEffect(() => {
        // получение всех пользователей
        const fetchUsers = async () => {
            setIsFetching(true);
            const res = await axios.get('/api/user');
            res.data.sort((a, b) => a.user_name.localeCompare(b.user_name));
            let filterUsers = filterUsersFoo(res.data);
            setUsers(filterUsers);
            setIsFetching(false);
        };

        // Вывод статистики для админов
        const getStatisticsAllUsers = async () => {
            if (localUser.isAdmin) {
                const getUsers = { id: localUser._id };
                const res = await axios.post('/api/get_statistics_all_users', getUsers);
                const all = res.data[0].get_statistics_all_users[0]['all_users'].sort((a, b) => a.user_name.localeCompare(b.user_name));
                let allUsers = filterUsersFoo(all);

                if (res.data[0].get_statistics_all_users[0]['all_users'] === null && document.location.href.includes('/all_users/')) {
                    alert('Нет не одного пользователя в работе');
                }
                if (res.data[0].get_statistics_all_users[0]['not_working_today'] === null && document.location.href.includes('/not_working_today/')) {
                    alert('Сегодня все в работе');
                    window.location.href = `/department/${localUser.departmentId}`;
                }
                if (res.data[0].get_statistics_all_users[0]['not_worked_yesterday'] === null && document.location.href.includes('/not_worked_yesterday/')) {
                    alert('Вчера все работали');
                    window.location.href = `/department/${localUser.departmentId}`;
                }
                if (res.data[0].get_statistics_all_users[0]['sick_today'] === null && document.location.href.includes('/sick_today/')) {
                    alert('Нет болеющих сегодня');
                    window.location.href = `/department/${localUser.departmentId}`;
                }
                if (res.data[0].get_statistics_all_users[0]['sick_yesterday'] === null && document.location.href.includes('/sick_yesterday')) {
                    alert('Вчера не было сотрудников на больничном');
                    window.location.href = `/department/${localUser.main_department}`;
                }
                if (res.data[0].get_statistics_all_users[0]['vacation_today'] === null && document.location.href.includes('/vacation_today')) {
                    alert('На сегодня нет сотрудников в отпуске');
                    window.location.href = `/department/${localUser.main_department}`;
                }
                if (res.data[0].get_statistics_all_users[0]['vacation_yesterday'] === null && document.location.href.includes('/vacation_yesterday')) {
                    alert('Вчера не было сотрудников в отпуске');
                    window.location.href = `/department/${localUser.main_department}`;
                }
                if (document.location.href.includes('/all_users/')) {
                    setIsJob(true);
                    setStatisticsWork(allUsers);
                }
                if (document.location.href.includes('/not_working_today/')) {
                    const notWorkingToday = res.data[0].get_statistics_all_users[0]['not_working_today'];
                    if (notWorkingToday !== null) {
                        setIsJob(true);
                        const sorted = notWorkingToday.sort((a, b) => a.user_name.localeCompare(b.user_name));
                        setStatisticsWork(filterUsersFoo(sorted));
                    }
                }
                if (document.location.href.includes('/not_worked_yesterday/')) {
                    const notWorkedYesterday = res.data[0].get_statistics_all_users[0]['not_worked_yesterday'];
                    if (notWorkedYesterday !== null) {
                        setIsJob(true);
                        const sorted = notWorkedYesterday.sort((a, b) => a.user_name.localeCompare(b.user_name));
                        setStatisticsWork(filterUsersFoo(sorted));
                    }
                }
                if (document.location.href.includes('/sick_today/')) {
                    const sickToday = res.data[0].get_statistics_all_users[0]['sick_today'];
                    if (sickToday !== null) {
                        setIsJob(true);
                        const sorted = sickToday.sort((a, b) => a.user_name.localeCompare(b.user_name));
                        setStatisticsWork(filterUsersFoo(sorted));
                    }
                }
                if (document.location.href.includes('/sick_yesterday/')) {
                    const sickYesterday = res.data[0].get_statistics_all_users[0]['sick_yesterday'];
                    if (sickYesterday !== null) {
                        setIsJob(true);
                        const sorted = sickYesterday.sort((a, b) => a.user_name.localeCompare(b.user_name));
                        setStatisticsWork(filterUsersFoo(sorted));
                    }
                }
                if (document.location.href.includes('/vacation_today/')) {
                    const vacationToday = res.data[0].get_statistics_all_users[0]['vacation_today'];
                    if (vacationToday !== null) {
                        setIsJob(true);
                        const sorted = vacationToday.sort((a, b) => a.user_name.localeCompare(b.user_name));
                        setStatisticsWork(filterUsersFoo(sorted));
                    }
                }
                if (document.location.href.includes('/vacation_yesterday/')) {
                    const vacationYesterday = res.data[0].get_statistics_all_users[0]['vacation_yesterday'];
                    if (vacationYesterday !== null) {
                        setIsJob(true);
                        const sorted = vacationYesterday.sort((a, b) => a.user_name.localeCompare(b.user_name));
                        setStatisticsWork(filterUsersFoo(sorted));
                    }
                }
            }
        };

        // функция показывает нажата ли галочка показать дочернии
        const statusChild = async () => {
            const user = await axios.get(`/api/user/${localUser._id}`);
            setChecked(user.data.see_child);
            setSeeChilds(user.data.see_child);
            setUser(user.data);
        };

        // Функция фиксирует всех пользователей если стоит галочка добавить дочернии в модальном окне
        const getUsersByDepIsCheckedTrue = async () => {
            const info = { depsId };
            const addchild = await axios.post('/api/get_users_by_dep/', info);
            const depUsers = addchild.data[0]['get_users_by_dep'];
            if (depUsers === null) {
                setUs([]);
            } else {
                depUsers.sort((a, b) => a.user_name.localeCompare(b.user_name));
                const filterUsers = filterUsersFoo(depUsers);
                setUs(filterUsers);
                setUsersDeps([]);
                setIsFetching(true);
            }
        };

        setIsJob(true);
        fetchUsers();
        getUsersByDepIsCheckedTrue();
        getStatisticsAllUsers();
        statusChild();
    }, [departId]);

    //Сортировка пользователей по статусам
    function filterUsersFoo(users) {
        return users.filter(user => user.status === 1)
            .concat(users.filter(user => user.status === 2))
            .concat(users.filter(user => user.status === 3))
            .concat(users.filter(user => user.status !== 1 && user.status !== 2 && user.status !== 3 && user.status !== 4))
            .concat(users.filter(user => user.status === 4));
    }


    // Добавление дочерних по checkbox
    const clickCheckbox = async () => {
        setChecked(!checked);
        const info = {
            userId: localUser._id,
            isChecked: !checked
        };
        await axios.post('/api/statusChild/', info

    );

        const inf = {
            depsId: depsId
        };
        const res = await axios.post('/api/get_users_by_dep/', inf);
        const depUsers = res.data[0]['get_users_by_dep'];

        if (depUsers === null) {
            setUs([]);
        } else {
            depUsers.sort((a, b) => a.user_name.localeCompare(b.user_name));
            let filterUsers = filterUsersFoo(depUsers);
            setUs(filterUsers);
        }

        if (checked) {
            setIsJob(!isJob);
        } else {
            setIsJob(isJob);
        }
    };

    // Обновляем main department по кнопке изменить в модальном окне
    const handleModalClick = async (e) => {
        e.preventDefault();

        const add = {
            id: localUser._id,
            depsId: depsId
        };

        await axios.put('/api/user', add);
        setModalMain(false);
    };

    // Функция get_users_by_dep по кнопке показать дочерние
    const getUsersByDepIsOpen = async () => {
        if (checked) {
            setUs([]);
        }

        if (isJob) {
            const info = { depsId: depsId };
            const res = await axios.post('/api/get_users_by_dep/', info);
            const depUsers = res.data[0]['get_users_by_dep'];

            if (depUsers !== null) {
                depUsers.sort((a, b) => a.user_name.localeCompare(b.user_name));
                let filterUsers = filterUsersFoo(depUsers);
                setUsersDeps(filterUsers);
                setUsers([]);
                setIsOpen(true);
                setIsJob(!isJob);
            }
        }
    };

    return (
        <div className='staff'>
            <div>
                {isJob ? (
                    <button onClick={getUsersByDepIsOpen} className="btnStaff" style={{ width: '155px' }}>
                        Показать дочерние
                    </button>
                ) : (
                    <button onClick={() => window.location.href = `/department/${departId}`} className="btnStaff">
                        Скрыть дочерние
                    </button>
                )}
                <button onClick={() => setModalMain(true)} className='btnStaff' style={{ float: 'right' }}>Сделать главной</button>
                {localUser.isAdmin && (
                    <Link to={`/VacanciesView/`}>
                        <button className="btnStaff">Показать вакансии</button>
                    </Link>
                )}
            </div>

            <div className="employeeWrapper">
                {user.main_department === departId && checked && !isOpen ?
                    us.map((user, id) => <Employee key={id} user={user} />) :
                    users.filter(user => user.department_id === departId).map((user, id) => <Employee key={id} user={user} />)
                }

                {!departId ? statisticsWork.map((user, id) => <Employee key={id} user={user} />) : ''}

                {isOpen && UsersDep.map((user, id) => <Employee key={id} user={user} />)}
            </div>

            <Modal active={modalMain} setActive={setModalMain}>
                <h1>Изменение информации:</h1>
                <hr />
                <form className="modalLoginBox" onSubmit={handleModalClick}>
                    <label>
                        <input
                            type='checkbox'
                            onChange={clickCheckbox}
                            checked={checked}
                        />
                        Добавить дочерние
                    </label>
                    <button className="ModalButtonDepartment" type="submit">
                        Сделать главным департаментом
                    </button>
                </form>
            </Modal>
        </div>
    );
}
