import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Sidebar.css';
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css';

const Sidebar = ({ initialOpenSubclassId }) => {

    const [dep, setDep] = useState([]);

    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/');
            setDep(res.data);
        };
        getDeps();
    }, []);

    // Функция для открытия подкласса по его id
    const openSubclassById = (id) => {
        const updatedDep = dep.map((node) => {
            const updatedNode = { ...node };
            updatedNode.department_tree_to_json.Subclasses.forEach((subclass) => {
                // Устанавливаем флаг isCollapsed в false для нужного подкласса
                subclass.isCollapsed = subclass.id !== id;
            });
            return updatedNode;
        });
        setDep(updatedDep);
    };

    return (
        <div className="sidebar">
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">
                        {dep.map((node, i) => {
                            const label = (
                                <Link to={`/department/${node.department_tree_to_json.id}`} className="level1">
                                    <span className="node">{node.department_tree_to_json.Name}</span>
                                </Link>
                            );
                            return (
                                <TreeView
                                    key={node.department_tree_to_json.Name + '|' + i}
                                    nodeLabel={label}
                                    defaultCollapsed={node.department_tree_to_json.id !== initialOpenSubclassId}
                                >
                                    {node.department_tree_to_json.Subclasses.map((departments) => (
                                        <TreeView
                                            nodeLabel={
                                                <Link
                                                    to={`/department/${departments.id}`}
                                                    className="depsUser"
                                                    key={departments.id}
                                                >
                                                    <span className="node">{departments.Name}</span>
                                                </Link>
                                            }
                                            key={departments.id}
                                            // Проверяем, открыт ли подкласс по его id
                                            defaultCollapsed={departments.id !== initialOpenSubclassId}
                                            onClick={() => openSubclassById(departments.id)}
                                        >
                                            {!!departments.Subclasses &&
                                            departments.Subclasses.map((departments1) => (
                                                <TreeView
                                                    nodeLabel={
                                                        <Link
                                                            to={`/department/${departments1.id}`}
                                                            className="depsUser"
                                                            key={departments1.id}
                                                        >
                                                            <span className="node">{departments1.Name}</span>
                                                        </Link>
                                                    }
                                                    key={departments1.id}
                                                    defaultCollapsed={true}
                                                >
                                                    {!!departments1.Subclasses &&
                                                    departments1.Subclasses.map((departments2) => (
                                                        <Link
                                                            to={`/department/${departments2.id}`}
                                                            className="depsUser"
                                                            key={departments2.id}
                                                        >
                                                            <div style={{ paddingBottom: '8px' }}>
                                                                {departments2.Name}
                                                            </div>
                                                        </Link>
                                                    ))}
                                                </TreeView>
                                            ))}
                                        </TreeView>
                                    ))}
                                </TreeView>
                            );
                        })}
                    </div>
                </div>
            </div>
            <div></div>
        </div>
    );
};

export default Sidebar;
