import { Search, ExitToApp } from '@material-ui/icons'
import axios from 'axios'
import { useEffect, useState } from 'react'
import SearchBlock from '../SearchBlock/SearchBlock'
import { Link } from 'react-router-dom'

import './Topbar.css'
import Status from '../Status/Status'

export default function Topbar() {
  const user = JSON.parse(localStorage.getItem('user'))
  const [searchValue, setSearchValue] = useState("")
  const [allUsers, setAllUsers] = useState([])
  const [findUsers, setFindUsers] = useState([])
  const [isBlock, setIsBlock] = useState(false)

  // выход
  const logOut = () => {
    localStorage.clear()
    window.location.reload()
  }

  // получение всех пользователей. отключено
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/users/all/1')
      setAllUsers(res.data)
    }
    fetchUser()
  }, [])

  // поиск пользователя. отключено
  const handleKeyPress = (e) => {
    if(e.key === 'Enter'){
      setFindUsers(allUsers.filter(user => user.username.toLowerCase().includes(searchValue.toLowerCase())))
      setIsBlock(true)
    }
  }

  return (
    <div>
      <div className='topbarContainer'>
        <div className="topbarLeft">
          <Status/>
        </div>
        <div className="topbarCenter">
          {/* <div className="searchbar">
            <Search/>
            <input value={searchValue} onChange={(e) => setSearchValue(e.target.value)} onKeyPress={handleKeyPress} placeholder='Найти...' className="searchInput" />
          </div> */}
        </div>
        <div className="topbarRight">
          <div className="topbarLinks">
            <Link to='/' className="topbarLink"><span >Главная страница</span></Link>
            <Link to={`/profile/${user._id}`} className="topbarLink"><span className="topbarLink">{user.username}</span></Link>
            <Link to='/information' className="topbarLink"><span >Информация</span></Link>
          </div>
          <ExitToApp style={{cursor:'pointer'}} onClick={logOut}/>
        </div>
      </div>
      {isBlock
        ? <div className="searchContainer">
            <div className='searchTop'>
              <h3 className='serachTitle'>Найдено: {findUsers.length}</h3>
              <button className='searchBtn' onClick={() => setIsBlock(false)}>Закрыть</button>
            </div>
            {findUsers.map((user) => <SearchBlock user={user}/>)}
          </div>
        : <></>
      }
    </div>
  )
}
