                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел сопровождения активных операций ФЛ",
                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел сопровождения пассивных операций ФЛ",
                              "Subclasses": []
                          }
                      ]
                  },
                  {
                      "Name": "Служба поддержски централизованных и технологических процессов",
                      "Subclasses": [
                          {
                              "Name": "Отдел поддержски обмена с ФОИВ",
                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел поддержски систем электронного хранения",
                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел поддержски технологиеских процессов",
                              "Subclasses": [
                                  {
                                      "Name": "Группа поддержки зарплатных проектов",
                                      "Subclasses": []
                                  },
                                  {
                                      "Name": "Группа поддержки логичесих карт и технологических сервисов",
                                      "Subclasses": []
                                  }
                              ]
                          },
                          {
                              "Name": "Отдел сопровождения централизированных приложений",
                              "Subclasses": []
                          }
                      ]
                  },
                  {
                      "Name": "Служба сопровождения муниципальных и специальных сервисов",
                      "Subclasses": [
                          {
                              "Name": "Отдел сопровождения муниципальных проектов",
                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел сопровождения специальных сервисов",
                              "Subclasses": []
                          }
                      ]
                  },
                  {
                      "Name": "Служба сопровождения операционной и расчетной деятельности",
                      "Subclasses": [
                          {
                              "Name": "Отдел поддержки пользователей прочих АБС",
                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел систем доставки электронных документов",
                              "Subclasses": [
                                  {
                                      "Name": "Группа поддежрски SWIFT/КФТ/TELEX",
                                      "Subclasses": []
                                  }
                              ]
                          },
                          {
                              "Name": "Отдел сопровождения АБС ЮЛ, систем расчетов и задач Казначейства",
                              "Subclasses": [
                                  {
                                      "Name": "Группа интеграции с внешними системами",
                                      "Subclasses": []
                                  },
                                  {
                                      "Name": "Группа поддержки пользователей восточной зоны РФ",
                                      "Subclasses": []
                                  },
                                  {
                                      "Name": "Группа поддержки пользователей европейской зоны РФ",
                                      "Subclasses": []
                                  },
                                  {
                                      "Name": "Группа сопровождения систем расчетов",
                                      "Subclasses": []
                                  }
                              ]
                          },
                          {
                              "Name": "Отдел сопровождения операционного дня",
                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел специального технологического сопровождения АБС",
                              "Subclasses": []
                          },
                          {
                              "Name": "Отдел управления изменениями",
                              "Subclasses": []
                          }
                      ]
                  }
              ]
          }
      }
  ]
console.log("dataSource=",dataSource)


  // загрузка всех департаментов
  useEffect(() => {
    // сортировка на дочерние и тд
    const getDeps = async () => {
        setIsFetching(true)
        const res = await axios.get('/api/departments/')
        for(let i = 0; i < res.data.length-1; i++){
          for(let j = i+1; j < res.data.length; j++){
            if(res.data.find(k => k === res.data[j].id && k !== res.data[i].id)){
              if(res.data[j].margin ){
                res.data[j].margin += 40
              } else {
                res.data[j].margin = 40
              }
            }
          }
        }

      let newArr = res.data.filter(d => d.id !== 7)
      newArr.push(res.data[6])
      setFilterDeps(newArr.filter(a => a.dep_id === 1))
      setDeps(newArr)
      if(currentUrl && currentUrl !== localUser._id){
        const arr1 = [1, 2, 3, 4, 5, 6, 7, '61efa2f6cf0ce39b27ba2a53', '61efa304cf0ce39b27ba2a54', '61efa31dcf0ce39b27ba2a55', '61efa32bcf0ce39b27ba2a56', '61efa33acf0ce39b27ba2a57', '61efa344cf0ce39b27ba2a58', '61efa34fcf0ce39b27ba2a59']
        const arr2 = [6, 8, 9, 10, 11, '61efa344cf0ce39b27ba2a58', '61efa35dcf0ce39b27ba2a5a', '61efa361cf0ce39b27ba2a5b', '61efa36acf0ce39b27ba2a5c', '61efa374cf0ce39b27ba2a5d']

        if(arr1.find(i => i === currentUrl)){
          setFilterDeps(newArr.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
        }
        if(arr2.find(i => i === currentUrl)){
          setFilterDeps(newArr)
        }else{
          setFilterDeps(newArr.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
        }
      }
      setIsFetching(false)
    }

    // загрузка пользователей и сортировка по департаментам
    const fetchUser = async () => {
      setIsFetching(true)
      const res = await axios.get('/api/user/')
      const id=res.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
      setUsers(res.data)
      setIsFetching(false)
      console.log("ID=",id)
    }

    fetchUser()
    getDeps()
  }, [])

  const isShowDeps = (e) => {
    const arr1 = [1, 2, 3, 4, 5, 6, 7, '61efa2f6cf0ce39b27ba2a53', '61efa304cf0ce39b27ba2a54', '61efa31dcf0ce39b27ba2a55', '61efa32bcf0ce39b27ba2a56', '61efa33acf0ce39b27ba2a57', '61efa344cf0ce39b27ba2a58', '61efa34fcf0ce39b27ba2a59']
    const arr2 = [6, 8, 9, 10, 11, '61efa344cf0ce39b27ba2a58', '61efa35dcf0ce39b27ba2a5a', '61efa361cf0ce39b27ba2a5b', '61efa36acf0ce39b27ba2a5c', '61efa374cf0ce39b27ba2a5d']

    if(e.target.value === 1){
      setFilterDeps(deps.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
    }
    if(arr2.find(i => i === e.target.value)){
      setFilterDeps(deps)
    }else{
      setFilterDeps(deps.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
    }
  }

  return (
    <div className='sidebar'>
      <div className="sidebarWrapper">
        <div className="tree">
          <div className="treeWrapper">

              {dataSource.map((node, i) => {

                  const label = <Link to={`/department/${node.id}`} className='depsUser' style={{fontWeight: `bold`}}><span className="node">{node.type}</span></Link>

                  return (

                      <TreeView key={node.type + '|' + i} nodeLabel={label} defaultCollapsed={true}>

                          {node.Subclasses.map(departments => {

                              const label2 = <Link to={`/department/${departments.id}`} className='depsUser'><span className="node">{departments.name}</span></Link>

                              return (

                                  <TreeView nodeLabel={label2} key={departments.child} defaultCollapsed={true}>

                                      {!!departments.child ? departments.child.map(departments1  => {

                                          const label3 = <Link to={`/department/${departments1.id}`} className='depsUser'><span className="node">{departments1.name}</span></Link>

                                          return(

                                              <TreeView nodeLabel={label3} defaultCollapsed={true} >

                                                  {!!departments1.child ? departments1.child.map(departments2  => {

                                            return(

                                                <Link to={`/department/${departments2.id}`} className='depsUser'>

