import React, {useEffect, useRef, useState} from 'react'
import Topbar from '../Topbar/Topbar'
import Pagination from "../Pagination/Pagination";
import ModalVacations from "../Templates/ModalVacations/ModalVacations"
import axios from "axios";
import './VacanciesView.css'
import VacanciesItem from "../VacanciesItem/VacanciesItem";




function VacanciesView() {
    const localUser = JSON.parse(localStorage.getItem('user'))
    const [modalVacations, setModalVacations] = useState(false)
    const [deps, setDeps] = useState([])
    const [find, setFind] = useState([])


    const [currentPage, setCurrentPage] = useState(1)
    const [postsPerPage] = useState(5)


    const [vacancies, setVacancies] = useState([])

    const lastPostIndex = currentPage * postsPerPage
    const firstPostIndex = lastPostIndex - postsPerPage
    const currentlyPosts = vacancies.slice(firstPostIndex, lastPostIndex)

    const lastPostIndexVacancies = currentPage * postsPerPage
    const firstPostIndexVacancies = lastPostIndexVacancies - postsPerPage
    const currentlyVacancies = find.slice(firstPostIndexVacancies, lastPostIndexVacancies)





    const paginate = pageNumber => setCurrentPage(pageNumber)




    const department = useRef()
    const searchRef = useRef()

    useEffect(() => {
        const VacationsView = async () => {
            const res = await axios.get('/api/departments/')
            setDeps(res.data)
            const getVacancies = await axios.get('/api/Vacations/')
            setVacancies(getVacancies.data)

        }

        VacationsView()
    }, [])


    const [searchValue, setSearchValue] = useState("")
    const [isOpen, setIsOpen] = useState(true)

    const clickwindow = (e) => {
        setModalVacations(true)

        console.log('value=',e.target.value)
    }

    const search = () => {
            setFind(vacancies.filter(vac => {
                return vac.name.toLowerCase().includes(searchValue.toLowerCase())
            }))
        setIsOpen(false)
    }

    const reset = () => {

        searchRef.current.value=''
    }
    const [inputValue, setInputValue] = useState("")
    return (
        <>
            <Topbar/>
            <div className='Vacations'>
                <div className="VacationsContainerTop">
                    <h2>Вакансии</h2>
                    <br/>
                    <div className="VacationsMain">
                        <div>
                    <input
                        type='search'
                        placeholder="Наименование вакансии"
                        className="ModalInput"
                        style={{'marginRight': '10px'}}
                        onChange={(e) => setSearchValue(e.target.value)}
                        ref={searchRef}
                    />
                    </div>
                        <div>
                        <select ref={department} className='depsView'>
                            {deps.map((dep, name) => <option selected={dep.id === localUser.departmentId} key={name} value={dep.id}>{dep.department_name}</option>)}
                        </select>
                        </div>
                            <div>
                        <button className="filterButton" onClick={search}>Найти</button>
                            </div>
                        <div>
                        <button className="filterButton" onClick={reset}>Сбросить</button>
                            </div>
                        </div>
                    <br/>

                   <div>
                    <input
                        type='date'
                        placeholder="Выберите дату"
                        className="ModalInput"

                        style={{'marginRight': '10px'}}
                    />
                        —  <input
                            type='date'
                            placeholder="Выберите дату"
                            className="ModalInput"
                            style={{'marginRight': '10px'}}
                        />
                    </div>
                </div>
                    {isOpen ? (
                        currentlyPosts.map((vacancies, id) => (
                            <div className='VacationsContainer' key={id} onClick={clickwindow}>
                                <VacanciesItem vacancies={vacancies}/>
                            </div>

                        ))
                    ) : (
                        <div>
                            {currentlyVacancies.map((vacancies, id) => (
                                <div className='VacationsContainer' key={id} onClick={() => setModalVacations(true)} >
                                    <VacanciesItem vacancies={vacancies}/>
                                </div>
                            ))}
                        </div>
                    )}
                </div>

            <div className='Pagination'>

                {isOpen ? <Pagination postsPerPage={postsPerPage} totalPosts={vacancies.length} paginate={paginate}
                       currentPage={currentPage} />:
                    <Pagination postsPerPage={postsPerPage} totalPosts={find.length} paginate={paginate}
                                                                currentPage={currentPage} />}

            </div>

            <ModalVacations active={modalVacations} setActive={setModalVacations}>
                <h1>Изменение информации:</h1>
                <hr/>
                <input
                    value={inputValue} // Передаем значение из состояния в value
                />
                <button className="ModalButton" type="submit">
                    Сохранить
                </button>
            </ModalVacations>
            <div>
            </div>

        </>

    )
}
export default VacanciesView
