import axios from "axios";
import { useEffect, useRef, useState } from "react";
import { Link, useHistory} from 'react-router-dom'

import "./Register.css";
const [register, setRegister] = useState(() => {
  return {
    username: "",
    email: "",
    password: "",
    password2: "",
  }
})

const changeInputRegister = event => {
  event.persist()
  setRegister(prev => {
    return {
      ...prev,
      [event.target.name]: event.target.value,
    }
  })
}


const submitChackin = event => {
  event.preventDefault();
  if(!validator.isEmail(register.email)) {
    alert("You did not enter email")
  } else if(register.password !== register.password2) {
    alert("Repeated password incorrectly")
  } else if(!validator.isStrongPassword(register.password, {minSymbols: 0})) {
    alert("Password must consist of one lowercase, uppercase letter and number, at least 8 characters")
  } else {
    axios.post(DOMEN_SERVER + "/auth/registration/", {
      username: register.username,
      email: register.email,
      password: register.password,
    }).then(res => {
      if (res.data === true) {
        window.location.href = DOMEN_SITE + "/auth"
      } else {
        alert("There is already a user with this email")
      }
    }).catch(() => {
      alert("An error occurred on the server")
    })
  }
}

export default function Register() {
  const email = useRef()
  const password = useRef()
  const passwordAgain = useRef()
  const username = useRef()
  const department = useRef()
  const [deps, setDeps] = useState([])
  const [error, setError] = useState('')
  const [checkVal, setCheckVal] = useState(false)
  const changeVal = (e) => {
    setCheckVal(!checkVal)
  }
  const history = useHistory()

  useEffect(() => {
    const getDeps = async () => {
      const res = await axios.get('/api/departments/')
      setDeps(res.data)
    }

    getDeps()
  }, [])

  const handleClick = async (e) => {
    e.preventDefault()
    if(password.current.value !== passwordAgain.current.value){
      passwordAgain.current.setCustomValidity('Пароли не совпадают')
    } else if(department.current.value === ''){
      setError('Не все поля заполнены')
      return
    } else {

      const user = {
        username: username.current.value,
        email: email.current.value,
        department: department.current.value,
        password: password.current.value,
        isadmin: checkVal
      }

      try {
        await axios.post('http://localhost:8080/api/createuser', {
          "user_name":"Егор",
          "password":"password",
          "email":"test@mail.ru",
          "isadmin":"False"

        })
        history.push('/login')
      } catch (error) {
        console.log(error);
      }
    }
  }

  return (
      <div className="form">
        <h2>Register user:</h2>
        <form onSubmit={submitChackin}>
          <p>Name: <input
              type="username"
              id="username"
              name="username"
              value={register.username}
              onChange={changeInputRegister}
          /></p>
          <p>Email: <input
              type="email"
              id="email"
              name="email"
              value={register.email}
              onChange={changeInputRegister}
              formnovalidate
          /></p>
          <p>Password: <input
              type="password"
              id="password"
              name="password"
              value={register.password}
              onChange={changeInputRegister}
          /></p>
          <p>Repeat password: <input
              type="password"
              id="password2"
              name="password2"
              value={register.password2}
              onChange={changeInputRegister}
          /></p>
          <input type="submit"/>
        </form>
      </div>
  )
}
