    const fetchUsers = async () => {
      setIsFetching(true)
      const res = await axios.get('/api/users/allusers/1')
      res.data.sort((a, b) => a.username.localeCompare(b.username))
      let filterUsers = filterUsersFoo(res.data)
      setUsers(filterUsers)
      setIsFetching(false)
    }

    // получение данных о себе
    const fetchUser = async () => {
      const res = await axios.get('/api/users/'+localUser._id)
      setUser(res.data)
      setIsFetching(false)
    }

    // получение и фильтр департаментов
    const getDeps = async () => {
      setIsModalFetching(true)
      const res = await axios.get('/api/departments/')
      addChecked(res.data)

      for(let i = 0; i < res.data.length-1; i++){
        for(let j = i+1; j < res.data.length; j++){
          if(res.data[i].children.find(k => k === res.data[j].dep_id && k !== res.data[i].dep_id)){
            if(res.data[j].margin ){
              res.data[j].margin += 10
            } else {
              res.data[j].margin = 10
            }
          }
        }
      }

      let newArr = res.data.filter(d => d.dep_id !== 7)
      newArr.push(res.data[6])

      setDeps(newArr)
      setIsModalFetching(false)
    }

    getDeps()
    fetchUser()
    setIsOpen(false)
    fetchUsers()
  }, [departId])

