  const handleAddChildren = e => {
    if (e.target.checked) {
      const currentDep = deps.filter(dep => departId ? dep._id === departId : dep._id === localUser.departmentId)
      addChildrenArr = deps.filter(dep => currentDep[0].children.find(i => i === dep.dep_id || i === currentDep[0].dep_id))
    }else{
      addChildrenArr = []
    }
  }

