      await axios.put('/api/users/'+localUser._id, newInfo)
      if(user._id === localUser._id){
        localStorage.setItem('user', JSON.stringify({...user, ...newInfo}))
      }
      window.location.reload()
    } catch (error) {
      console.log(error);
    }
  }

