const AuthReducer = (state, action) => {
  switch(action.type){
    case 'LOGIN_START':
      return {
        user: null,
        posts: null,
        isFetching: true,
        error: false,
        err: false
      }
    case 'LOGIN_SUCCESS':
      return {
        user: action.payload,
        posts: null,
        isFetching: false,
        error: false,
        err: state.error,
      }
    case 'LOGIN_FAIL':
      return {
        user: null,
        posts: null,
        isFetching: false,
        error: action.payload,
        err: action.payload,
      }
    default:
      return state
  }
}

export default AuthReducer
