import Topbar from '../Topbar/Topbar'
import Sidebar from "../Sidebar/Sidebar";
import {Link} from "react-router-dom";
import React, {useState} from "react";
import VacanciesView from "../VacanciesView/VacanciesView";
import './AdminPanel_Vacancies.css'
import VacanciesAdd from "../VacanciesAdd/VacanciesAdd";

export default function  AdminPanel_Vacancies() {
    const [isOpenVacancies, setIsOpenVacancies] = useState(false);
    const [vacationAdd, setVacationAdd] = useState(false);
    const [hideButtons, setHideButtons] = useState(false);
    const [openAdd, setIsOpenAdd] = useState(false);
    const [isOpen, setIsOpen] = useState(false);

    const handleShowVacancies = () => {
        setIsOpen(true);
        setHideButtons(true);
        setIsOpenVacancies(true);
        setIsOpenAdd(false);
    }

    const handleReturn = () => {
        setIsOpen(false);
        setHideButtons(false);
        setIsOpenVacancies(true); // Устанавливаем обратно, чтобы VacanciesView снова появилась
        setVacationAdd(false); // Скрываем VacanciesAdd
    }

    const clickAdd = () => {
        setIsOpenAdd(true);
        setIsOpenVacancies(false);
        setVacationAdd(true);
        setHideButtons(true); // Устанавливаем, чтобы показать кнопку "Вернуться"
    }

    return (
        <div className="admin-panel1">
            <Topbar />
            <div className="content11">
                <Sidebar />
                <div className="main-content11">
                    <div>
                        {hideButtons ? (
                            <button className="btnStaff" onClick={handleReturn} style={{ width: '155px' }}>
                                Вернуться
                            </button>
                        ) : (
                            <button className="btnStaff" onClick={clickAdd} style={{ width: '155px' }}>
                                Добавить Вакансию
                            </button>
                        )}
                    </div>
                    {isOpenVacancies && (
                        <>
                            {hideButtons ? <VacanciesAdd/> : <VacanciesView/>}
                        </>
                    )}
                </div>
            </div>
        </div>
    )
}
