import React, {useEffect, useRef, useState} from "react";

import './VacanciesAdd.css'
import axios from "axios";

function VacanciesAdd({users}) {

    const localUser = JSON.parse(localStorage.getItem('user'))
    const [deps, setDeps] = useState([])
    const [userId, setUserId] = useState([])
    const [error, setError] = useState('')
    const [message, setMessage] = useState('')
    const [isView, setIsView] = useState(false)

    const [status, setStatus] = useState([])
    const [company, setCompany] = useState([])
    const [checkVal, setCheckVal] = useState(false)
    const NameVacationRef=useRef()
    const TeamRef = useRef()
    const StatusRef = useRef()
    const CodeVacations = useRef()
    const Company = useRef()
    const Description = useRef()
    const CodeProject = useRef()
    const Grade = useRef()





    const changeVal = () => {
        setCheckVal(!checkVal)
    }
    useEffect(() => {
        const getDeps = async () => {
            const departament = await axios.get('/api/departments/')
            setDeps(departament.data)
            const status = await axios.get('/api/Vacations/?status=status')
            setStatus(status.data)
            const company = await axios.get('/api/Vacations/?company=company')
            setCompany(company.data)
        }


        getDeps()
    }, [])


    const handleUserClick = (userName,userId) => {
        setUserId(userId)
        setSearchTerm(userName)
        setFilteredUsers([])
    }

    const [searchTerm, setSearchTerm] = useState('');

    const [filteredUsers, setFilteredUsers] = useState([]);


    const handleInputChange = (e) => {
        const term = e.target.value;
        setSearchTerm(term);
        const filtered = users.filter(user =>
            user.user_name && user.user_name.toLowerCase().includes(term.toLowerCase())
        );
        if (term === '') {
            setFilteredUsers([])
        } else {
            setFilteredUsers(filtered);
        }
    }



    const clickHandler = async () => {
        let userNameToSend = ''
        if (searchTerm !== '') {
            userNameToSend = userId
        }
        const Vacations = {
            namevacancies: NameVacationRef.current.value,
            description: Description.current.value,
            team_id: TeamRef.current.value,
            status_id: StatusRef.current.value,
            codeVacations: CodeVacations.current.value,
            codeProject: CodeProject.current.value,
            company_id: Company.current.value,
            grade: Grade.current.value,
            is_checked: checkVal,
            userName: userNameToSend
        }
        if (NameVacationRef.current.value === '' || Description.current.value === '' || TeamRef.current.value === '' ||
            StatusRef.current.value === '' || CodeVacations.current.value === '' || CodeProject.current.value === '' ||
            Grade.current.value === '' || Company.current.value === '') {
            setError("Заполните поля")
            setIsView(true)
        } else {
            const res = await axios.post('/api/Vacations/', Vacations)
            if (res.status === 200) {
                setMessage('Добавленно')
                setError('')
            }
        }
    }

    const clearInput = () => {
        Description.current.value = ''
        NameVacationRef.current.value=''
        CodeVacations.current.value=''
        CodeProject.current.value=''
        Grade.current.value=''
    }

    return (
        <>
                    <br/>
                    <div className="VacationsMainAdd">
                        <h2 style={{textAlign:'center'}}>Добавить вакансию</h2>
                        {
                            !error && isView && message &&
                            <div>Добавленно</div>
                        }
                        {
                            message && !error && !isView &&
                            <div>Добавленно</div>
                        }
                        {
                            error && isView &&
                            <div>Заполните поле</div>
                        }
                        <div>
                        </div>
                        <div>
                            <input ref={NameVacationRef} type='text' placeholder='Наименование вакансии' className='InputVacation'/>
                        </div>
                        <div>
                            <textarea ref={Description} placeholder='Описание вакансии' className='InputVacationAdd'/>
                        </div>
                        <div>
                            <select ref={TeamRef} className='SelectVacation'>
                                {deps.map((dep, name) => <option selected={dep.id === localUser.departmentId} key={name}
                                                                 value={dep.id}>{dep.department_name}</option>)}
                            </select>
                        </div>
                        <div>
                        <select ref={StatusRef} className='SelectVacation'>
                            {status.map((status, name) => <option key={name} value={status.id}>{status.name}</option>)}
                        </select>
                        </div>
                        <div>
                            <input ref={CodeVacations} type='text' placeholder='Код вакансии' className='InputVacation'/>
                        </div>
                        <div className='inputDiv'>
                            <input
                                type='text'
                                placeholder='Имя сотрудника'
                                className='InputVacation'
                                value={searchTerm}
                                onChange={handleInputChange}
                            />
                            <div className='divSelect'>
                                {filteredUsers.length > 0 && (
                                    <div>
                                        {filteredUsers.map((user, index) => (
                                            <div className='selectNameDiv' key={index} value={user.id} onClick={() => handleUserClick(user.user_name, user.id)}>
                                                {user.user_name}
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        </div>

                        <div>
                            <select ref={Company} className='SelectVacation'>
                                <option value=''>Компания</option>
                                {company.map((company, name) => <option key={name} value={company.id}>{company.name}</option>)}
                            </select>
                        </div>
                        <div>
                            <input ref={CodeProject} type='number' placeholder='Код проекта' className='InputVacation'/>
                        </div>
                        <div>
                            <input ref={Grade} type='number' placeholder='Грейд' className='InputVacation'/>
                        </div>
                        <div style={{marginTop:'20px'}}>
                        <label style={{paddingRight:'10px'}}>Нужна вакансия после завершения проекта?</label>
                        <label htmlFor='Vacation'>
                            <input
                            type='radio'
                            value={true}
                            id='Vacation'
                            name='VacationTrue'
                            checked={checkVal}
                            onChange={changeVal}
                        />Да</label>
                        <label htmlFor='Vacation1'>
                            <input
                            type='radio'
                            value={false}
                            id='Vacation'
                            name='VacationFalse'
                            checked={!checkVal}
                            onChange={changeVal}
                        />Нет</label>
                        </div>
                        <div style={{textAlign:'center',paddingTop:"20px"}}>
                            <button className="filterButton" onClick={clickHandler}>Сохранить</button>
                            <button className="filterButton" onClick={clearInput}>Сбросить</button>
                            {/*<button className="filterButton" onClick={() => window.location.href = `/department/${depsId.depsId}`} >Выход</button>*/}
                        </div>
                    </div>
        </>
    )
}
export default VacanciesAdd
