import { useContext } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import Error from "./pages/Error/Error";
import Login from "./pages/Login/Login";
import Profile from "./pages/Profile/Profile";
import Register from "./pages/Register/Register";
import Department from './pages/Department/Department';
import Information from "./pages/Information/Information";
import Reports from "./pages/Reports/Reports";
import ReportsNominations from "./pages/ReportsNominations/ReportsNominations";
import ReportsHistory from "./pages/ReportsHistory/ReportsHistory";
import Message from "./components/MessagePost/MessagePost";
import VacanciesView from "./components/VacanciesView/VacanciesView";
import VacanciesAdd from "./components/VacanciesAdd/VacanciesAdd";
import AdminPanel from './components/AdminPanel/AdminPanel';

function App() {
  const { user } = useContext(AuthContext);
  const userStorage = JSON.parse(localStorage.getItem('user'));

  const authUser = user || userStorage;

  return (
      <Router>
        <Routes>
          <Route
              path="/"
              element={authUser ? <Navigate to={`/department/${authUser.main_department}`} /> : <Navigate to="/login" />}
          />
          <Route
              path="/login"
              element={authUser ? <Navigate to={`/department/${authUser.main_department}`} /> : <Login />}
          />
          <Route
              path="/register"
              element={authUser ? <Navigate to="/" /> : <Register />}
          />
          <Route
              path="/profile/:username"
              element={authUser ? <Profile /> : <Login />}
          />
          <Route
              path="/department/:username"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/information"
              element={authUser ? <Information /> : <Login />}
          />
          <Route
              path="/reports"
              element={authUser ? <Reports /> : <Login />}
          />
          <Route
              path="/reportsnominations"
              element={authUser ? <ReportsNominations /> : <Login />}
          />
          <Route
              path="/reportshistory"
              element={authUser ? <ReportsHistory /> : <Login />}
          />
          <Route
              path="/all_users"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/not_working_today"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/not_worked_yesterday"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/sick_today"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/sick_yesterday"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/vacation_today"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/vacation_yesterday"
              element={authUser ? <Department /> : <Login />}
          />
          <Route
              path="/message"
              element={authUser ? <Message /> : <Login />}
          />
          <Route
              path="/vacanciesview"
              element={authUser ? <VacanciesView /> : <Login />}
          />
          <Route
              path="/vacanciesadd"
              element={authUser ? <VacanciesAdd /> : <Login />}
          />
          <Route
              path="/adminpanel"
              element={authUser ? <AdminPanel /> : <Login />}
          />
          <Route
              path="*"
              element={<Error />}
          />
        </Routes>
      </Router>
  );
}

export default App;
