import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'

const Sidebar = () => {
    const [dep, setDep] = useState([]);
    const [openedNodes, setOpenedNodes] = useState({});
    const initialNodeId = 21; // Здесь задайте ID узла, который нужно открыть по умолчанию

    useEffect(() => {
        const getDeps = async () => {
            try {
                const res = await axios.get('/api/department_tree_to_json/');
                if (res.data && res.data.length > 0) {
                    setDep(res.data);

                    // Создаем объект с открытыми узлами, включая заданный узел
                    let initialOpenedNodes = {};
                    res.data.forEach(node => {
                        initialOpenedNodes[node.department_tree_to_json.id] = node.department_tree_to_json.id === initialNodeId;
                        node.department_tree_to_json.Subclasses.forEach(department => {
                            initialOpenedNodes[department.id] = department.id === initialNodeId;
                            if (department.Subclasses) {
                                department.Subclasses.forEach(subdepartment => {
                                    initialOpenedNodes[subdepartment.id] = subdepartment.id === initialNodeId;
                                });
                            }
                        });
                    });
                    setOpenedNodes(initialOpenedNodes);
                } else {
                    setDep([]);
                }
            } catch (error) {
                console.error('Error fetching departments:', error);
            }
        };

        getDeps();
    }, [initialNodeId]);

    const handleNodeClick = (nodeId) => {
        const updatedNodes = { ...openedNodes };
        updatedNodes[nodeId] = !openedNodes[nodeId];
        setOpenedNodes(updatedNodes);
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                {dep.length >= 1 ? (
                    <div className="tree">
                        <div className="treeWrapper">
                            {dep.map((node, i) => {
                                const label = (
                                    <TreeView
                                        key={`${node.department_tree_to_json.Name}|${i}`}
                                        nodeLabel={(
                                            <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                                                <span className="node">{node.department_tree_to_json.Name}</span>
                                            </Link>
                                        )}
                                        defaultCollapsed={true}
                                        onClick={() => handleNodeClick(node.department_tree_to_json.id)}
                                    >
                                        {node.department_tree_to_json.Subclasses.map((departments) => (
                                            <TreeView
                                                nodeLabel={(
                                                    <Link to={`/department/${departments.id}`} className='depsUser' key={departments.id}>
                                                        <span className="node">{departments.Name}</span>
                                                    </Link>
                                                )}
                                                key={departments.id}
                                                defaultCollapsed={!openedNodes[departments.id]}
                                                onClick={() => handleNodeClick(departments.id)}
                                            >
                                                {!!departments.Subclasses && departments.Subclasses.map((departments1) => (
                                                    <TreeView
                                                        nodeLabel={(
                                                            <Link to={`/department/${departments1.id}`} className='depsUser' key={departments1.id}>
                                                                <span className="node">{departments1.Name}</span>
                                                            </Link>
                                                        )}
                                                        key={departments1.id}
                                                        defaultCollapsed={!openedNodes[departments1.id]}
                                                        onClick={() => handleNodeClick(departments1.id)}
                                                    >
                                                        {!!departments1.Subclasses && departments1.Subclasses.map((departments2) => (
                                                            <Link to={`/department/${departments2.id}`} className='depsUser' key={departments2.id}>
                                                                <div style={{ paddingBottom: '8px' }}>{departments2.Name}</div>
                                                            </Link>
                                                        ))}
                                                    </TreeView>
                                                ))}
                                            </TreeView>
                                        ))}
                                    </TreeView>
                                );

                                return label;
                            })}
                        </div>
                    </div>
                ) : (
                    ''
                )}
            </div>
        </div>
    );
};

export default Sidebar;
