              {name: 'Служба поддержски централизованных и технологических процессов',id:7,
                  child: [
                      {name: 'Отдел поддержски обмена с ФОИВ',id:8},
                      {name: 'Отдел поддержски систем электронного хранения',id:9},
                      {name: 'Отдел поддержски технологиеских процессов',id:10,
                          child: [
                              {name:'Группа поддержки зарплатных проектов',id:11,},
                              {name:'Группа поддержки логичесих карт и технологических сервисов',id:12,}
                          ]},
                      {name: 'Отдел сопровождения централизованных процессов',id:13}
                  ],
              },
              {name: 'Служба сопровождения муниципальных и специальных сервисов',id:14,
                  child: [
                      {name: 'Отдел сопровождения муниципальных проектов',id:15},
                      {name: 'Отдел сопровождения специальных сервисов',d:16}
                  ],
              },
