                          child: [
                              {name:'Группа интеграции с внешними системами',id:22},
                              {name:'Группа поддержки пользователей восточной зоны РФ',id:23},
                              {name:'Группа поддержки пользователей европейской зоны РФ',id:24},
                              {name:'Группа сопровождения систем расчетов',id:25}]},
                      {name: 'Отдел сопровождения операционного дня',id:26},
