import './InfoPost.css'

export default function InfoPost({post, username}) {
   /*console.log(post)*/
  // перевод даты в читабельный вид
  let dateStart = new Date(post.workStart)
  let dateEnd = new Date(post.workEnd)
  let workTime = new Date(post.workTime)
  let AllTimeWork = new Date(workTime - 3*60*60*1000)
  dateStart = dateStart.toLocaleString()
  dateEnd = dateEnd.toLocaleString()
  AllTimeWork = AllTimeWork.toLocaleTimeString()
  dateStart = dateStart.split(',')
  dateEnd = dateEnd.split(',')

  return (
    <div  className='InfoPosts'>
      <div className={`InfoPost`}>
        <div className={`infoPostTable`}>
          <div className='infoPostTableBlock Data'>
            {post.workdate}
          </div >
          <div className='infoPostTableBlock Start'>
            {post.workstart}
          </div>
          <div className='infoPostTableBlock End'>
            {post.workend}
          </div>
          <div className='infoPostTableBlock Time'>
            {post.worklong}
          </div>
          <div className='infoPostTableBlock Health'>
            {post.healthstart === '0' ? 'Болен' : 'Здоров'}
            <br/>
            {post.healthend === '1' ? 'Здоров' : 'Болен'}
          </div>
          <div className='infoPostTableBlock Health'>
            <div>
            {post.workcomment}</div>
            <br/>
          </div>
        </div>
      </div>
      <hr/>
    </div>
  )
}
