import React, {useEffect, useRef, useState} from 'react'
import Pagination from "../Pagination/Pagination";
import ModalVacancies from "../Templates/ModalVacancies/ModalVacancies"
import axios from "axios";
import './VacanciesView.css'
import VacanciesItem from "../VacanciesItem/VacanciesItem";



function VacanciesView(departId) {
    const localUser = JSON.parse(localStorage.getItem('user'))
    const [vacanciesId, setVacanciesId] = useState("")

    const [modalVacations, setModalVacations] = useState(false)

    const [inputValue, setInputValue] = useState("")


    const [inputValueDescription, setInputValueDescription] = useState("")

    const [inputValueStatuses, setInputValueStatuses] = useState("")

    const [inputValueGrade, setInputValueGrade] = useState("")



    const [selectedVacanciesName, setSelectedVacanciesName] = useState('');

    const [selectedVacanciesStatuses, setSelectedVacanciesStatuses] = useState('');
    const [selectedVacanciesDescription, setSelectedVacanciesDescription] = useState('');
    const [selectedVacanciesGrade, setSelectedVacanciesGrade] = useState('');


    const [searchValue, setSearchValue] = useState("")
    const [isOpen, setIsOpen] = useState(true)

    const [currentPage, setCurrentPage] = useState(1)
    const [postsPerPage] = useState(5)

    const [currentPageVacancies, setCurrentPageVacancies] = useState(1)
    const [postsPerPageVacancies] = useState(5)

    const [vacancies, setVacancies] = useState([])
    const [find, setFind] = useState([])

    const lastPostIndex = currentPage * postsPerPage
    const firstPostIndex = lastPostIndex - postsPerPage
    const currentlyPosts = vacancies.slice(firstPostIndex, lastPostIndex)

    const lastPostIndexVacancies = currentPageVacancies * postsPerPageVacancies
    const firstPostIndexVacancies = lastPostIndexVacancies - postsPerPageVacancies
    const currentlySearch = find.slice(firstPostIndexVacancies, lastPostIndexVacancies)

    const paginate = pageNumber => setCurrentPage(pageNumber)
    const paginateVacancies = pageNumberVacancies => setCurrentPageVacancies(pageNumberVacancies)


    const statusRef = useRef()

    const searchRef = useRef()



    const [startDate, setStartDate] = useState(''); // начальная дата фильтра
    const [endDate, setEndDate] = useState(''); // конечная дата фильтра


    const [filteredData, setFilteredData] = useState([]);

    const [status, setStatus] = useState([])


    useEffect(() => {
        const VacanciesView = async () => {
            const status = await axios.get('/api/Vacations/?status=status')
            setStatus(status.data)

            const getVacancies = await axios.get('/api/Vacations/')

            const getVacancies1 = await axios.get('/api/Vacations_function/')

            const sort = getVacancies1.data[0].get_vacancies['department'].sort((a, b) => a.depth - b.depth)

            console.log('sort=',sort)

            const sortedVacancies = getVacancies.data.sort((a, b) => b.create_date.localeCompare(a.create_date));

            if (isOpen) {
                // Фильтрация вакансий по departId
                const filteredVacancies = sortedVacancies.filter(item => item.team_id === departId.departId);
                setVacancies(sort); // Установка нового массива вакансий
            }
        }

        VacanciesView()
    }, [isOpen, departId])


    const handleClick = (name, grade,id,description,statuses) => {
        if(localUser.isAdmin===true) {
            setModalVacations(true);
            setVacanciesId(id)

            setSelectedVacanciesName(name);
            setInputValue(name); // Установка начального значения для name


            setSelectedVacanciesDescription(description)
            setInputValueDescription(description)

            setSelectedVacanciesGrade(grade);
            setInputValueGrade(grade); // Установка начального значения для grade

            setSelectedVacanciesStatuses(statuses)
            setInputValueStatuses(statuses)

        }

    };

    // Обработчик изменения значения для поля ввода с именем
    const handleNameInputChange = (e) => {
        setInputValue(e.target.value);
    }

    // Обработчик изменения значения для поля ввода с grade
    const handleDescriptionInputChange = (e) => {
        setInputValueDescription(e.target.value);
    }

    const handleGradeInputChange = (e) => {
        setInputValueGrade(e.target.value);
    }
    const handleStatusesInputChange = (e) => {
        setInputValueStatuses(e.target.value);
    }

    const reset = () => {
        searchRef.current.value = ''
        setSearchValue('')
        setStartDate('')
        setEndDate('')
    }


    const handleStartDateChange = (e) => {
        setStartDate(e.target.value)
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value)
    }

    const selectValue = () => {
        setInputValueStatuses(statusRef.current.value)
    }


    const handleCombinedFilter = () => {
        if (searchValue.length === 0 && !startDate && !endDate) {
            // Если нет поискового запроса и не выбраны даты, просто отобразите сообщение об ошибке или что-то подобное
            setFilteredData([]);
            setFind([]);
            setIsOpen(true)
            return
        }

        if (searchValue.length !== 0) {
            // Если есть поисковой запрос, выполняем фильтрацию по нему
            setFind(vacancies.filter(vac => {
                return vac.name.toLowerCase().includes(searchValue.toLowerCase());
            }));
            setFilteredData([]);
            setIsOpen(false);
        } else {
            // Если нет поискового запроса, выполняем фильтрацию по дате
            setFilteredData(vacancies.filter(item => {
                const itemDate = new Date(item.create_date);
                const start = startDate ? new Date(startDate) : null;
                const end = endDate ? new Date(endDate) : null;
                return (!start || itemDate >= start) && (!end || itemDate <= end);
            }));
            setFind([]);
            setIsOpen(false);
        }
    }


    const ChangeData = async () => {

    const ChangeData ={
    namevacanciesId:vacanciesId,
    namevacancies:inputValue,
    description:inputValueDescription,
    Statuses:inputValueStatuses,
    grade:inputValueGrade,
    statuses:statusRef.current.value
        }
        const res = await axios.put('/api/Vacations/', ChangeData)
    }

    return (
        <>
            <div className='Vacations'>
                <div className="VacationsContainerTop">
                    <h2>Вакансии</h2>
                    <br/>
                    <div className="VacationsMain">

                            <input
                                type='search'
                                placeholder="Наименование вакансии"
                                className="ModalInput"
                                style={{'marginRight': '10px'}}
                                onChange={(e) => setSearchValue(e.target.value)}
                                ref={searchRef}
                            />
{/*                            <select ref={department} className='depsView'>
                                {deps.map((dep, name) => <option selected={dep.id === departId.departId} key={name} value={dep.id}>{dep.department_name}</option>)}
                            </select>*/}
                    </div>
                        <br/>
                    <div>
                        <input
                            type='date'
                            placeholder="Выберите дату"
                            className="ModalInput"
                            style={{'marginRight': '10px'}}
                            value={startDate} onChange={handleStartDateChange}
                        />—   <input
                        type='date'
                        placeholder="Выберите дату"
                        className="ModalInput"
                        style={{'marginRight': '10px'}}
                        value={endDate} onChange={handleEndDateChange}
                    />
                            <button className="filterButton" onClick={handleCombinedFilter}>Поиск</button>
                            <button className="filterButton" onClick={reset}>Сбросить</button>
                    </div>

                </div>
                {isOpen ? (
                    vacancies.map((vac, id) => (
                        <div className='VacationsContainer' key={id} >
                            <VacanciesItem key={vac.department} department={vac.department} vacancies={vac.vacancies} vac={vac}/>
                        </div>
                    ))
                ) : (
                    <div>
                        {currentlySearch.map((vac, id) => (<div className='VacationsContainer' key={id} onClick={() => handleClick(vac.name, vac.grade,vac.id,vac.description,vac.status_id)}>
                                <VacanciesItem vac={vac} departId={departId.departId}/>
                            </div>
                        ))}
                    </div>
                )}
                {filteredData.map(vac => (
                    <div className='VacationsContainer' key={vac.id}
                         onClick={() => handleClick(vac.name, vac.grade)}>
                        <VacanciesItem vac={vac}/>
                    </div>
                    ))}
            </div>
            <br/>
                <div style={{textAlign:'center'}}>
{/*                {isOpen ?
                    <Pagination postsPerPage={postsPerPage} totalPosts={vacancies.length} paginate={paginate}
                                currentPage={currentPage}/>
                    :

                    <Pagination postsPerPage={postsPerPageVacancies} totalPosts={find.length}
                                paginate={paginateVacancies}
                                currentPage={currentPageVacancies}/>}*/}
                </div>


            <ModalVacancies active={modalVacations} setActive={setModalVacations}>
                <hr/>
                <div>
                    <input type='text' className="ModalInputVacancies"
                           value={inputValue}
                           onChange={handleNameInputChange}
                    />
                </div>
                <div>
                    <textarea  className='textDescription'
                           value={inputValueDescription}
                           onChange={handleDescriptionInputChange}
                    />
                </div>
                <div>
                    Статус
                </div>
                <div>
                    <select ref={statusRef} value={inputValueStatuses} onChange={selectValue}>
                        {status.map((status, name) => <option key={name} value={status.id}>{status.name}</option>)}
                    </select>
                </div>


                <div>
                    <div>
                    <label>Грейд</label>
                    </div>
                    <input type='number'
                           value={inputValueGrade}
                           onChange={handleGradeInputChange}
                    />
                </div>
                <br/>
                <div className='divHistory'>
                    История
                </div>
                <br/>
                <div>
                <button className="ModalButton" type="submit" onClick={ChangeData}>
                    Сохранить
                </button>
                </div>
            </ModalVacancies>
            <div>
            </div>

        </>

    )
}

export default VacanciesView
