import { useEffect, useState } from 'react'
import axios from 'axios'

import Employee from '../Employee/Employee'
import './Staff.css'
import { useParams } from 'react-router-dom'
import Modal from '../Templates/Modal/Modal'

export default function Staff() {
  const [users, setUsers] = useState([])
  const [user, setUser] = useState([])
  const [deps, setDeps] = useState([])
  const [isOpen, setIsOpen] = useState(false)
  const [isFetching, setIsFetching] = useState(false)
  const departId = useParams().username

  const localUser = JSON.parse(localStorage.getItem('user'))

  const [modalActive, setModalActive] = useState(false)
  const [modalMain, setModalMain] = useState(false)
  const changedArr = []
  const [isModalFetching, setIsModalFetching] = useState(false)

  for(let i=0; i<localUser.checkedDeps?.length; i++){
    changedArr.push(localUser.checkedDeps[i])
  }

  // ставит галочки в модалке
  function addChecked(deps){
    for(let i=0; i < deps.length; i++){
      for(let j=0; j < localUser.checkedDeps?.length; j++){
        if(deps[i]._id === localUser.checkedDeps[j]){
          deps[i].checked = true
        }
      }
    }
  }

  // фильтр нужных департаментов
  /*const currentDep = deps.filter(dep => departId ? dep._id === departId : dep._id === localUser.departmentId)
  let filterDeps = deps.filter(dep => currentDep[0].children.find(i => i === dep.dep_id || i === currentDep[0].dep_id))
  filterDeps.push(...currentDep)*/

  // фильтр нужных людей
  /*const usersFilter = users.filter(user => filterDeps.find(f => f._id === user.departmentId))
  const usersFilterDeps = users.filter(user => localUser.checkedDeps?.find(f => f === user.departmentId))
  const depsFilterDeps = deps.filter(dep => localUser.checkedDeps?.find(f => f === dep._id))*/

  const isLockDeps = localUser.checkedDeps !== null && localUser.checkedDeps?.length > 0

  let addChildrenArr = []

  useEffect(() => {

    // получение всех пользователей
    const fetchUsers = async () => {
      setIsFetching(true)
      const res = await axios.get('/api/user')
      res.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
      let filterUsers = filterUsersFoo(res.data)
      setUsers(filterUsers)
      setIsFetching(false)
    }

    // получение данных о себе
    const fetchUser = async () => {

      const res = await axios.get('/api/user/'+localUser._id)

      setUser(res.data)
      setIsFetching(false)
    }

    // получение и фильтр департаментов
    const getDeps = async () => {
      setIsModalFetching(true)
      const res = await axios.get('/api/departments/')
      addChecked(res.data)

      for(let i = 0; i < res.data.length-1; i++){
        for(let j = i+1; j < res.data.length; j++){
          if(res.data.find(k => k === res.data[j].dep_id && k !== res.data[i].dep_id)){
            if(res.data[j].margin ){
              res.data[j].margin += 10
            } else {
              res.data[j].margin = 10
            }
          }
        }
      }

      let newArr = res.data.filter(d => d.dep_id !== 7)
      newArr.push(res.data[6])

      setDeps(newArr)
      setIsModalFetching(false)
    }

    getDeps()
    fetchUser()
    setIsOpen(false)
    fetchUsers()
  }, [departId])

  // фильтр пользователей по статусу
  function filterUsersFoo(users){
    return users.filter(user => user.status === 1)
    .concat(users.filter(user => user.status === 2))
    .concat(users.filter(user => user.status === 3))
    .concat(users.filter(user => user.status !== 1 && user.status !== 2 && user.status !== 3 && user.status !== 4))
    .concat(users.filter(user => user.status === 4))
  }

  const handleClick = () => {
    if(departId){
      setIsOpen(true)
    }else{
      setModalActive(true)
    }
  }

  // добавляем/удаляем департамент в модалке
  const handlePush = e => {
    if (e.target.checked) {
      changedArr.push(e.target.value)
    }else{
      const index = changedArr.indexOf(e.target.value)
      changedArr.splice(index, 1)
      delete e.target.checked
    }
  }

  // добавление дочерних
  const handleAddChildren = e => {
    if (e.target.checked) {
      const currentDep = deps.filter(dep => departId ? dep._id === departId : dep._id === localUser.departmentId)
      addChildrenArr = deps.filter(dep => currentDep[0].children.find(i => i === dep.dep_id || i === currentDep[0].dep_id))
    }else{
      addChildrenArr = []
    }
  }

  // обновляем данные модального окна
  const handleModalClick = async (e) => {
    e.preventDefault()

    let newInfo

    if(departId){
      addChildrenArr = addChildrenArr.map(i => i._id)
      newInfo= {
        userId: user._id,
        checkedDeps: [departId, ...addChildrenArr]
      }
    }else{
      newInfo= {
        userId: user._id,
        checkedDeps: changedArr
      }
    }

    try {
      await axios.put('/api/user/'+localUser._id, newInfo)
      if(user._id === localUser._id){
        localStorage.setItem('user', JSON.stringify({...user, ...newInfo}))
      }
      window.location.reload()
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div className='staff'>
      { !departId && <div className='changeDepsContainer'>
        <div><span className='changeDeps' onClick={handleClick}>Изменить</span></div>
      </div>}
      {departId && <>
        <button onClick={handleClick} className='btnStaff'>Показать дочерние</button>
        <button onClick={() => setIsOpen(false)} className='btnStaff'>Скрыть дочерние</button>
        <button onClick={() => setModalMain(true)} className='btnStaff' style={{float: 'right'}}>Сделать главной</button>
      </>}
      <div className="employeeWrapper">
        {isFetching
          ? <div className='isFetching'>Загрузка...</div>
          : users.length === 0 
            ? <div className='emptyStaff'>Спиcок пуст</div> 
            : departId 
              ? !isOpen 
                ? users.filter(user => user.departmentId === departId).map((user, id) => <Employee key={id} user={user}/>)
                : usersFilter.map((user, id) => <Employee key={id} user={user}/>)
              : isLockDeps 
                ? usersFilterDeps.map((user, id) => <Employee key={id} user={user}/>) 
                : users.map((user, id) => <Employee key={id} user={user}/>) 
        }
      </div>
      <Modal active={modalActive} setActive={setModalActive}>
        <h1>Изменение информации:</h1>
        <hr/>
        <form className="modalLoginBox">
          {isModalFetching 
            ?<div>Загрузка</div>
            : deps.map((dep, id) => <label key={id} htmlFor={dep.departmentName} style={{paddingLeft: `${dep.margin}px`}}>
            <input
              type='checkbox'
              value={dep._id}
              id={dep.departmentName}
              name={dep.departmentName}
              onChange={handlePush}
              checked={dep.checked}
            />{dep.departmentName}</label>)
          }
          <button className="ModalButton" type="submit" disabled={isFetching} onClick={handleModalClick}>
            Изменить
          </button>
        </form>
      </Modal>
      <Modal active={modalMain} setActive={setModalMain}>
        <h1>Изменение информации:</h1>
        <hr/>
        <form className="modalLoginBox">
          <label>
            <input
              type='checkbox'
              onChange={handleAddChildren}
            />Добавить дочерние</label>
          <button className="ModalButton" type="submit" disabled={isFetching} onClick={handleModalClick}>
            Изменить
          </button>
        </form>
      </Modal>
    </div>
  )
}
