import { Link } from 'react-router-dom'
import { Delete } from '@material-ui/icons'
import axios from 'axios'

import './Employee.css'
import { useEffect, useState } from 'react'

export default function Employee({user}) {
  const localUser = JSON.parse(localStorage.getItem('user'))

  const usersStatuses = {0: '', 1: 'start', 2: 'end', 5: 'sick_leave', 4: 'deleted' ,3: 'vacation',6: 'time_off',7: 'other'}
  
  function findStatus(k){
    for(let i in usersStatuses){
      if(i == k){
        return usersStatuses[i]
      }
    }
  }

  const [date, setDate] = useState(null)
  const [time, setTime] = useState(null)

  // Проверка, если пользователь закончил работу, то вывод даты, когда он закончил
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/post/')
      res.data.sort((p1, p2) => {
        return new Date(p2.workStart) - new Date(p1.workStart)
      })
/*      let data = (res.data[0].workEnd)
      data = new Date(data)
      data = data.toLocaleString()
      data = data.split(',')
      let dataF = data[0].split('.')
      let dataS = data[1].split(':')
      dataF = dataF[0]+'/'+dataF[1]
      dataS = dataS[0]+':'+dataS[1]
      setDate(dataF)
      setTime(dataS)*/
    }
    if(user.status === 2){
      fetchUser()
    }
  }, [user._id])

  const [dateStart, setDateStart] = useState(null)
  const [dateEnd, setDateEnd] = useState(null)

  // Проверка, если пользователь в отпуске или на больничном, то вывод даты начала и конца
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/post?id='+localUser._id)

/*      res.data.sort((p1, p2) => {
        return new Date(p2.start) - new Date(p1.start)
      })*/
      global.xz=res.data[0].workdate
      global.xx=res.data[0].worked

      console.log('RES=',global.xz ,"-", global.xx)
    }

    if(user.status === 3){
      fetchUser()
    }
  }, [user._id])

  return (<>
    {user.status === 4 && !localUser.isAdmin
      ? <div></div> 
        : <Link to={`/profile/${user.id}`} className={'employee ' + (findStatus(user.status))}>
          <div className='top'>
            {localUser.isAdmin
              ? <Link to={`/profile/${user.id}`} className='name'><span></span></Link>
              : <div className='name'><span>{user.user_name}</span></div>
            }
          </div>
          <div className='divname'>{user.user_name}</div>
          <div className='down'>
              <div className='downRight'>
                {/*<div className='emailEmployee'>{user.email}</div>*/}
                {user.status === 2 && <div>{date} {time}</div>}
                {user.status === 3 && <div>{global.xz} - {global.xx}</div>}
                </div>
          </div>
        </Link>
    }</>
  )
}
