import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import { Link, useHistory} from 'react-router-dom'
import "./Register.css";

export default function Register() {
  const email = useRef()
  const password = useRef()
  const passwordAgain = useRef()
  const username = useRef()
  const firstname = useRef()
  const department = useRef()
  const history = useHistory()
  const telephone=useRef()
  const city=useRef()
  const additional_contact=useRef()
  const distribution_group=useRef()
  const link_vacancies=useRef()
  const activity_profile=useRef()


  const [deps, setDeps] = useState([])
  const [error, setError] = useState('')
  const [checkVal, setCheckVal] = useState(false)
  const [vacancies, setVacancies] = useState([])
  const [searchTerm, setSearchTerm] = useState('');


  const handleInputChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    const filtered = vacancies.filter(user =>
        user.user_name && user.user_name.toLowerCase().includes(term.toLowerCase())
    );
    if (term === '') {
      setVacancies([])
    } else {
      setVacancies(filtered);
    }
  }


  const handleUserClick = (userName,userId) => {
    setUserId(userId)
    setSearchTerm(userName)
    setVacancies([])
  }

  const changeVal = (e) => {
    setCheckVal(!checkVal)
  }





  useEffect(() => {
    const getDeps = async () => {
      const res = await axios.get('/api/departments/')
      setDeps(res.data)

      const getVacancies = await axios.get('/api/Vacations/?vacancies=vacancies')
      console.log('getVacancies=',getVacancies.data)
      setVacancies(getVacancies.data)
    }

    getDeps()
  }, [])

  const handleClick = async (e) => {
    e.preventDefault()
    if(password.current.value !== passwordAgain.current.value){
      passwordAgain.current.setCustomValidity('Пароли не совпадают')
    } else if(department.current.value === ''){
      setError('Не все поля заполнены')
      return
    } else {

      const name = firstname.current.value+' '+username.current.value

      const user = {
        user_name: name,
        email: email.current.value.toUpperCase(),
        department_id: department.current.value,
        password: password.current.value,
        isadmin: checkVal,
        telephone:telephone.current.value,
        city:city.current.value,
        additional_contact:additional_contact.current.value,
        distribution_group:distribution_group.current.value,
        link_vacancies:link_vacancies.current.value,
        activity_profile:activity_profile

      }

        const res = await axios.post('/api/Register/', user)
        if (res.data.status === "Username taken"){
          setError("Такой email уже существует")
        }else{
          await axios.post('/api/Register/', user)
          history.push('/login')
        }

    }
  }

  function handleSubmit(e) {
    if (e.target.value === '') {
      setError('Email должен содержать формат домена VTB')
    }
    if (e.target.value.length > 0) {
      setError('')
    }
  }

  function handleClickSubmit() {
setError('')
  }

  return (

      <div className="register">
        <br/>
        <div className="registerWrapper">
          <div className="registerCenter">
            <form className="registerBox" onSubmit={handleClick}>
              {error &&
              <div>
                {error}
              </div>
              }
              <input
                  placeholder="Имя"
                  required ref={username}
                  className="registerInput"
                  onMouseDown={handleClickSubmit}
              />
              <input
                  placeholder="Фамилия"
                  required ref={firstname}
                  className="registerInput"
                  onMouseDown={handleClickSubmit}
              />

              <input
                  placeholder="Телефон"
                  type='number'
                  required ref={telephone}
                  className="registerInput"
                  onMouseDown={handleClickSubmit}
              />
              <input
                  placeholder="Город"
                  required ref={city}
                  className="registerInput"
                  onMouseDown={handleClickSubmit}
              />
              <input
                  placeholder="Доп контакт"
                  required ref={additional_contact}
                  className="registerInput"
                  onMouseDown={handleClickSubmit}
              />
              <input
                  placeholder="Группа рассылки"
                  required ref={distribution_group}
                  className="registerInput"
                  onMouseDown={handleClickSubmit}
              />
              <input
                  placeholder="Ссылка на вакансию"
                  required ref={link_vacancies}
                  className="registerInput"
                  value={searchTerm}
                  onChange={handleInputChange}
                  onMouseDown={handleClickSubmit}
              />
              <div className='divSelect'>
                {vacancies.length > 0 && (
                    <div>
                      {vacancies.map((vac, id) => (
                          <div className='selectNameDiv' key={vac.id} value={vac.id} onClick={() => handleUserClick(vac.name, vac.id)}>
                            {vac.name}
                          </div>
                      ))}
                    </div>
                )}
              </div>
              <input
                  placeholder="Профиль деятельности"
                  required ref={activity_profile}
                  className="registerInput"
                  onMouseDown={handleClickSubmit}
              />
              <input
                  placeholder="Email в формате IvanoIA@vtb.ru"
                  type='email'
                  required ref={email}
                  className="registerInput"
                  onMouseDown={handleSubmit}
                  onInput={handleSubmit}
              />
            <select ref={department} className='selectDeps' >
              <option value=''>Выберите подразделение</option>
              {deps.map((dep, name) => <option key={name} value={dep.id}>{dep.department_name}</option>)}
            </select>
            <div>
              Начальник отдела?
              <label htmlFor='trueAdmin'>
              <input
                type='radio'
                value={true}
                name='trueAdmin'
                checked={checkVal}
                onChange={changeVal}
                onMouseDown={handleClickSubmit}
              />Да</label>
              <label htmlFor='falseAdmin'>
              <input
                type='radio'
                value={false}
                name='falseAdmin'
                checked={!checkVal}
                onChange={changeVal}
                onMouseDown={handleClickSubmit}
              />Нет</label>
            </div>
            <input
              placeholder="Password"
              type='password'
              required ref={password}
              className="registerInput"
              minLength='6'
              onMouseDown={handleClickSubmit}
            />
            <input
              placeholder="Password Again"
              type='password'
              required ref={passwordAgain}
              className="registerInput"
              minLength='6'
              onMouseDown={handleClickSubmit}
            />
            <button className="registerButton" type='submit'>Зарегистрироваться</button>

            <Link to='/login' style={{width:'167%'}}>

              <button className="registerLoginButton">
                Войти в аккаунт
              </button>
            </Link>
          </form>
        </div>
      </div>
    </div>
  );
}
