import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const Sidebar = () => {
    const [departments, setDepartments] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('/api/department_tree_to_json/');
                setDepartments(response.data);
            } catch (error) {
                console.error('Error fetching departments:', error);
            }
        };

        fetchData();
    }, []);

    const renderSubclasses = (subclasses) => {
        return (
            <Accordion>
                {subclasses.map((subclass) => (
                    <Card key={subclass.id}>
                        <Accordion.Toggle as={Card.Header} eventKey={subclass.id.toString()}>
                            {subclass.Name}
                        </Accordion.Toggle>
                        <Accordion.Collapse eventKey={subclass.id.toString()}>
                            <Card.Body>
                                {subclass.Subclasses.length > 0 ? renderSubclasses(subclass.Subclasses) : 'No subdepartments'}
                            </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                ))}
            </Accordion>
        );
    };

    return (
        <div>
            <h2>Department Tree</h2>
            <Accordion>
                {departments.map((department) => (
                    <Card key={department.id}>
                        <Accordion.Toggle as={Card.Header} eventKey={department.id}>
                            {department.Name}
                        </Accordion.Toggle>
                        <Accordion.Collapse eventKey={department.id}>
                            <Card.Body>
                                {department.Subclasses.length > 0 ? renderSubclasses(department.Subclasses) : 'No departments'}
                            </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                ))}
            </Accordion>
        </div>
    );
};
export default Sidebar;
