import './RepostsInfo.css'
import {Link} from "react-router-dom";
import {useState} from "react";

export default function RepostsInfo({posts}) {
    const [sortedData, setSortedData] = useState(posts);
    const [sortDirection, setSortDirection] = useState('asc');
    const sortByColumn = (column) => {
        const newData = [...sortedData];
        newData.sort((a, b) => {
            if (sortDirection === 'asc') {
                return a[column] > b[column] ? 1 : -1;
            } else {
                return a[column] < b[column] ? 1 : -1;
            }
        });
        setSortedData(newData);
        setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    };
    return (
        <tr>
            <th onClick={() => sortByColumn('name')}>Name</th>
            <th onClick={() => sortByColumn('age')}>Age</th>
            <th onClick={() => sortByColumn('email')}>Email</th>
        </tr>
    )
}
