import { useEffect, useRef, useState } from 'react'
import axios from 'axios'
import {CircularProgress} from '@material-ui/core'
import Modal from '../Templates/Modal/Modal'
import './Status.css'

const usersStatuses = {0: '', 1: 'start', 2: 'end', 3: 'sick_leave', 4: 'deleted' ,5: 'vacation', 6: 'time_off', 7: 'other'}
function findStatus(k){
  for(let i in usersStatuses){
    if(i == k){
      return usersStatuses[i]
    }
  }
}
export default function Status() {
  const [deee,setDel] = useState([])
  const [workplace,setworkplace] = useState([])
  const [works,setWorks] = useState([])
  const [user, setUser] = useState([])
  const localUser = JSON.parse(localStorage.getItem('user'))
  const [isFetching, setIsFetching] = useState(false)
  const [error, setError] = useState('')
  const [modalActive, setModalActive] = useState(false)
  const [modalActiveReserve, setModalActiveReserve] = useState(false)
  const [modalName, setModalName] = useState()
  const modalComment = useRef()
  const Place = useRef()
  const DataTime = useRef()




  // получение данных пользователя о работе
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/user/'+localUser._id)
      const place = await axios.get('/api/workplaces/')
      const work = await axios.get('/api/workplace_logs_get/'+localUser._id)
      const filter = work.data.filter(element => element.is_canceled !== true)
      console.log('work=',work.data)
      setworkplace(filter)
      setUser(res.data)
      setWorks(place.data)
    }
    fetchUser()
  }, [localUser._id])

  // начало работы.
  const handlerStart = async (e) => {
    e.preventDefault()
    if (+modalName === 0 && modalComment.current.value === '') {
      setError('Заполните комментарий')
      return
    }
      if (+modalName !== 0 && +modalName !== 1) {
        setError('Заполните состояние здоровья')
    } else {
        setIsFetching(true)
        const newPost = {
          userId: localUser._id,
          typework_id: 1,
          status: 1,
          healthStart: +modalName,
          commentStart: modalComment.current.value || '-',
          workStart: new Date(Date.now())
        }
        try {
          const res = await axios.post("/api/post/", newPost)

          /*await axios.post("/api/status/")*/
          /*await axios.put('/api/user/'+user._id, {status:1, userId: user._id})*/
          localStorage.setItem('user', JSON.stringify({...localUser, status: 1}))
          window.location.reload()
        } catch (error) {
          console.log(error)
        }
      }
  }


  // конец работы
  const handlerEnd = async (e) => {
    e.preventDefault()
    if (+modalName === 0 && modalComment.current.value === '') {
      setError('Заполните комментарий')
      return
    }
    if (+modalName !== 0 && +modalName !== 1) {
      setError('Заполните состояние здоровья')
    } else {
      setIsFetching(true)
      const newPost = {
        userId: localUser._id,
        typework_id: 1,
        status: 2,
        healthEnd: +modalName,
        commentEnd: modalComment.current.value || '-',
        workEnd: new Date(Date.now())
        /*workTime: new Date(Date.now()) - new Date(posts[0].workStart)*/
      }

      try {
        await axios.put("/api/post/", newPost)
        /*await axios.put('/api/user/'+user._id, {status:2, userId: user._id})*/
        localStorage.setItem('user', JSON.stringify({...localUser, status: 2}))
        window.location.reload()
      } catch (error) {
        console.log(error)
      }
    }
  }

  const handleName = e => {
    setModalName(e.target.value)
  }

  const clickReserve =  async () => {
    if(Place.current.value==='' || DataTime.current.value===''){
    }else {
      const reservePlace = {
        user_id: localUser._id,
        wp_id: Place.current.value,
        state: works[0].location,
        booking_date:DataTime.current.value,
        is_canceled: false
      }
      await axios.post("/api/workplace_logs/", reservePlace)
      const work = await axios.get('/api/workplace_logs_get/'+localUser._id)
      const filter = work.data.filter(element => element.is_canceled !== true)
      console.log('work=',work.data)
      setworkplace(filter)
    }
  }
const clearPlace =async ()=>{
  const is_canceled={
    user_id:localUser._id,
    canceled:true
  }
  await axios.post('/api/workplace_logs_clear/',is_canceled)
  const work = await axios.get('/api/workplace_logs_get/'+localUser._id)
  const filter = work.data.filter(element => element.is_canceled !== true)
  setworkplace(filter)

}
  const searchPlace =async ()=>{
console.log('data=',DataTime.current.value)
  }

const deletePlace = async (e) => {
  e.preventDefault()
  console.log('e=',e.target.value)
  const is_canceled={
    user_id:localUser._id,
    id:e.target.value,
    canceled:true
  }
  console.log('e=',e.target.value)
  await axios.post('/api/workplace_logs_post/',is_canceled)
  const work = await axios.get('/api/workplace_logs_get/'+localUser._id)
  const filter = work.data.filter(element => element.is_canceled !== true)
  setworkplace(filter)

}

/*  const deletePlace = async (e) => {
    e.preventDefault()
    console.log('e=',e.target.value)
    const del =  await axios.delete('/api/workplace_logs_delete/'+e.target.value)
    const work = await axios.get('/api/workplace_logs_get/'+localUser._id)
    setworkplace(work.data)
  }*/


  return (
    <>
      <table border="0">
        <tr>
          <td><button type='submit' onClick={() => setModalActive(true)} className={'statusBtn ' + (findStatus(user.status))}>
            {isFetching
                ? <CircularProgress style={{color:'white', size:'20px', width:'20px', height:'20px'}}/>
                : user.status !== 1
                    ? 'Начать'
                    : 'Закончить'
            }
          </button></td>
          <td><button type='submit' onClick={() => setModalActiveReserve(true)} className='statusBtn'>
            <h5>Зарезервировать место</h5>
          </button></td>
        </tr>
      </table>

      <Modal active={modalActive} setActive={setModalActive}>
        <h1>Изменение информации:</h1>
        <hr/>
        {error && <div className='modalError'>{error}</div>}
        <form className="modalLoginBox" onSubmit={user.status === 1 ? handlerEnd : handlerStart}>
        <div style={{display:'flex', justifyContent:'space-between', fontSize: '20px', width:'30%', marginLeft:'35%', marginTop: '30px'}}>
            <label htmlFor='sickLeave'>
            <input
              type='radio'
              value={1}
              id='sickLeave'
              name='sickLeave'
              onChange={handleName}
              checked={+modalName === 1}
            />Здоров</label>
            <label htmlFor='vacation'>
            <input
              type='radio'
              value={0}
              id='vacation'
              name='vacation'
              onChange={handleName}
              checked={+modalName === 0}
            />Болен</label>
          </div>
          <input
            placeholder="Комментарий"
            className="ModalInput"
            ref={modalComment}
          />
          <button className="ModalButton" type="submit" disabled={isFetching}>
            ок
          </button>
        </form>
      </Modal>

        <Modal active={modalActiveReserve} setActive={setModalActiveReserve}>
          <h1>Зарезервировать рабочее место:</h1>
          <hr/>
              <div>
                  <div style={{height:'250px'}}>
                    <div>
                      <button onClick={clearPlace}>Отчистить</button>
                    </div>
                   {workplace.map((array,id) =>
                            <div className='place'>Место: {array.place} Дата: {array.booking_date}
                          <button className='placeButton' key={id} value={array.id} onClick={deletePlace}>Снять резерв</button>
                            </div> )}
                  </div>
                <hr/>
                <br/>
                <div>
                  <div>
                  <input
                      type='date'
                      className="ModalInput"
                      ref={DataTime}
                      style={{'marginRight': '10px'}}
                      onChange={searchPlace}
                  />
                  </div>
                  <br/>
                  <hr/>
                 <h3>БЦ Башня ВТБ (Евразия) 22.37</h3>
                  <select ref={Place}
                          value={works.id}
                          className='selectwork'
                          onClick={searchPlace}>

                    {works.map((work, id) => <option title={work.place} key={id} value={works.id}>{work.place}</option>)}

                  </select>
                </div>
            <hr/>
          </div>
          <button className="ModalButton" onClick={clickReserve} type="submit" disabled={isFetching}>
              ок
            </button>
      </Modal>
    </>
  )
}
