              if(res.data[j].margin ){
                res.data[j].margin += 40
              } else {
                res.data[j].margin = 40
              }
            }
          }
        }*/



  }

  return (
    <div className='sidebar'>
      <div className="sidebarWrapper">
        <div className="tree">
          <div className="treeWrapper">
            {isFetching
              ? <CircularProgress style={{color:'#1877f2', margin:'30%'}}/>
              : filterDeps.map((dep, id) => <SidebarDeps/>)
            }

            {deps.map((dep, id) =>
                <ul className="tree" id="tree">
                  <li key={id}>{dep.department_name}{dep.parent_department}</li>
                </ul>)}
          </div>
        </div>
      </div>
    </div>
  )
}
