import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = ({ targetDepartmentId }) => {
    targetDepartmentId=21
    const [dep, setDep] = useState([])
    // загрузка всех департаментов
    useEffect(() => {

        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/')
            if(res.data[0].department_tree_to_json.length===0){
                setDep([])
            }else {
                setDep(res.data)
            }
        }
        getDeps()
    }, [])

    useEffect(() => {
        if (targetDepartmentId) {
            const findAndExpandDepartment = (departments) => {
                for (const department of departments) {
                    if (department.department_tree_to_json.id === targetDepartmentId) {
                        return [department.department_tree_to_json.id];
                    }
                    if (department.department_tree_to_json.Subclasses) {
                        const result = findAndExpandDepartment(department.department_tree_to_json.Subclasses);
                        if (result) {
                            return [department.department_tree_to_json.id, ...result];
                        }
                    }
                }
                return null;
            };

            const expandedDepartments = findAndExpandDepartment(deps);
            if (expandedDepartments) {
                setExpandedDepartments(expandedDepartments);
            }
        }
    }, [dep, targetDepartmentId]);

    const [expandedDepartments, setExpandedDepartments] = useState([]);

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">
                        {dep.map((node, i) => {
                            const label = (
                                <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                                    <span className="node">{node.department_tree_to_json.Name}</span>
                                </Link>
                            );
                            return (
                                <TreeView
                                    key={node.department_tree_to_json.Name + '|' + i}
                                    nodeLabel={label}
                                    defaultCollapsed={!expandedDepartments.includes(node.department_tree_to_json.id)}>
                                    {node.department_tree_to_json.Subclasses.map((department) => (
                                        <TreeView
                                            key={department.id}
                                            nodeLabel={(
                                                <Link to={`/department/${department.id}`} className='depsUser' key={department.id}>
                                                    <span className="node">{department.Name}</span>
                                                </Link>
                                            )}
                                            defaultCollapsed={!expandedDepartments.includes(department.id)}>
                                            {!!department.Subclasses && department.Subclasses.map((department1) => (
                                                <TreeView
                                                    key={department1.id}
                                                    nodeLabel={(
                                                        <Link to={`/department/${department1.id}`} className='depsUser' key={department1.id}>
                                                            <span className="node">{department1.Name}</span>
                                                        </Link>
                                                    )}
                                                    defaultCollapsed={!expandedDepartments.includes(department1.id)}>
                                                    {!!department1.Subclasses && department1.Subclasses.map((department2) => (
                                                        <Link to={`/department/${department2.id}`} className='depsUser' key={department2.id}>
                                                            <div style={{paddingBottom: '8px'}}>{department2.Name}</div>
                                                        </Link>
                                                    ))}
                                                </TreeView>
                                            ))}
                                        </TreeView>
                                    ))}
                                </TreeView>
                            );
                        })}
                    </div>
                </div>
            </div>
            <div></div>
        </div>
    );
};
export default Sidebar;
