import axios from "axios";
import { useEffect, useRef, useState } from "react";
import { Link, useHistory} from 'react-router-dom'

import "./Register.css";

export default function Register() {

  const email = useRef()
  const password = useRef()
  const passwordAgain = useRef()
  const username = useRef()
  const department = useRef()
  const [deps, setDeps] = useState([])
  const [error, setError] = useState('')
  const [checkVal, setCheckVal] = useState(false)
  const changeVal = (e) => {
    setCheckVal(!checkVal)
  }
  const history = useHistory()

  useEffect(() => {
    const getDeps = async () => {
      const res = await axios.get('/api/departments/')
      setDeps(res.data)
    }

    getDeps()
  }, [])

  const handleClick = async (e) => {
    e.preventDefault()
    if(password.current.value !== passwordAgain.current.value){
      passwordAgain.current.setCustomValidity('Пароли не совпадают')
    } else if(department.current.value === ''){
      setError('Не все поля заполнены')
      return
    } else {

      const user = {
        username: username.current.value,
        email: email.current.value,
        department: department.current.value,
        password: password.current.value,
        isadmin: checkVal
      }

      try {
        await axios.post('/api/createuser/', user)
        history.push('/login')
      } catch (error) {
        console.log(error);
      }
    }
  }

  return (
    <div className="register">
      {error &&
        <div className='error'>
          {error}
        </div>
      }
      <div className="registerWrapper">
        <div className="registerCenter">
          <form className="registerBox" onSubmit={handleClick}>
            <input
              placeholder="Фамилия Имя"
              required ref={username}
              className="registerInput"
            />
            <input
              placeholder="Email"
              type='email'
              required ref={email}
              className="registerInput"
            />
            <select ref={department}>
              <option value=''>Отдел</option>

              {deps.map((dep, name) =>

              <option key={name} value={dep.department_id}>{dep.department_name}</option>)}

            </select>
            <div>
              Начальник отдела?
              <label htmlFor='trueAdmin'>
              <input
                type='radio'
                value={true}
                name='trueAdmin'
                checked={checkVal}
                onChange={changeVal}
              />Да</label>
              <label htmlFor='falseAdmin'>
              <input
                type='radio'
                value={false}
                name='falseAdmin'
                checked={!checkVal}
                onChange={changeVal}
              />Нет</label>
            </div>
            <input
              placeholder="Password"
              type='password'
              required ref={password}
              className="registerInput"
              minLength='6'
            />
            <input
              placeholder="Password Again"
              type='password'
              required ref={passwordAgain}
              className="registerInput"
              minLength='6'
            />
            <button className="registerButton" type='submit'>Зарегистрироваться</button>

            <Link to='/login' style={{width:'167%'}}>

              <button className="registerLoginButton">
                Войти в аккаунт
              </button>
            </Link>
          </form>
        </div>
      </div>
    </div>
  );
}
